const initialState = {
  email: "",
  password: "",
  token: null,
  //auth: false,
  // error: "",
  // errorStatus: false,
  // avatar: '',

};

const authLogin = (state = initialState, action) => {
  switch (action.type) {

    case 'LOGIN_START':
      return { ...state, error: "", errorStatus: false };

    case 'LOGIN_SUCCESS':
    // return {
    //     ...state,
    //     token: action.resp.token,
    //     auth: true,
    //     error:"",
    //     errorStatus: false,
    //     username: action.resp.username,
    //     avatar: action.resp.avatar
    // }
    case 'LOGIN_FAIL':
      return {
        ...state,
        errorStatus: true,
        error: action.resp
      }
    default:
      return state;
  }
}

export default authLogin
