import React, { Component } from 'react';
import { Grid, withStyles, TextField, Button, Typography } from '@material-ui/core';
import Container from '@material-ui/core/Container';
import { auth } from '../../firebase';
import { withRouter } from 'react-router-dom';

const style = (theme) => ({
  root: {
    padding: '10px 4px',
    display: 'flex'
  },
  button: {
    margin: theme.spacing(1),
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
  },
})
class Login extends Component {
  constructor(props) {
    super(props)
    this.state = {
      email: '',
      password: '',
      error: ''
    }
  }
  render() {
    const { classes } = this.props;
    const { email, password, error } = this.state
    return (
      <Container maxWidth="sm">
        <Grid container justify="center" className="my-16">
          <Grid item xs={12}>
            <Typography variant="h5" component="h6" className="text-center">
              Sign In
            </Typography>

            {error &&
              <p className="text-center text-red-800"> {error}</p>
            }
          </Grid>

          <Grid item xs={12}>
            <TextField
              id="outlined-email-input"
              label="Email"
              className={classes.textField}
              value={email}
              type="email"
              name="email"
              autoComplete="email"
              fullWidth
              margin="normal"
              variant="outlined"
              onChange={(e) => this.handleChange(e)}
              required
            />
          </Grid>
          <Grid item xs={12} >
            <TextField
              id="outlined-email-input"
              label="Password"
              className={classes.textField}
              type="password"
              value={password}
              fullWidth
              name="password"
              autoComplete="password"
              margin="normal"
              variant="outlined"
              onChange={(e) => this.handleChange(e)}
              required
            />
          </Grid>
          <Grid item xs={12} >
            <Button variant="outlined" fullWidth onClick={this.handleSubmit} color="primary" className={`${classes.button} uppercase`}>
              Login
            </Button>
          </Grid>
        </Grid>
      </Container>
    )
  }

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  componentWillMount() {
    if (localStorage.getItem('user')) {
        this.props.history.push('/');
    }
}

  handleSubmit = (e) => {
    e.preventDefault()
    var email = this.state.email;
    var password = this.state.password;
    auth.signInWithEmailAndPassword(email, password)
      .then(data => {
        localStorage.setItem("user", data.user.email)
        localStorage.setItem("user_id", data.user.uid)
        this.props.history.push("/")
      })
      .catch((error) => {
        console.log("error", error);
        this.setState({
          error: error.message
        })
      });
  }
}
export default withStyles(style)(withRouter(Login));
